import React, { useState } from 'react';
import axios from 'axios';
import { Oval } from 'react-loader-spinner';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFrown } from '@fortawesome/free-solid-svg-icons';
import './App.css';

function Grp204WeatherApp() {
    const [input, setInput] = useState('');
    const [weather, setWeather] = useState({
        loading: false,
        data: null,
        error: false,
    });

    const toDateFunction = () => {
        const months = [
            'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin',
            'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre',
        ];
        const weekdays = [
            'Dimanche', 'Lundi', 'Mardi', 'Mercredi', 'Jeudi', 'Vendredi', 'Samedi',
        ];
        const currentDate = new Date();
        const date = `${weekdays[currentDate.getDay()]} ${currentDate.getDate()} ${months[currentDate.getMonth()]}`;
        return date;
    };

    const search = async (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            if (!input.trim()) {
                setWeather({ ...weather, error: true });
                return;
            }
            setWeather({ loading: true, data: null, error: false });
            const url = 'https://api.openweathermap.org/data/2.5/weather';
            const apiKey = 'f00c38e0279b7bc85480c3fe775d518c';

            try {
                const response = await axios.get(url, {
                    params: {
                        q: input,
                        units: 'metric',
                        appid: apiKey,
                    },
                });
                setWeather({ loading: false, data: response.data, error: false });
                setInput(''); // Clear the input field
            } catch (err) {
                setWeather({ loading: false, data: null, error: true });
            }
        }
    };

    return (
        <div className="App">
            <h1 className="app-name">Application Météo </h1>
            <div className="search-bar">
                <input
                    type="text"
                    className="city-search"
                    placeholder="Entrez le nom de la ville..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={search} // Changed to onKeyDown for better cross-browser compatibility
                />
            </div>

            {weather.loading && (
                <div className="loader">
                    <Oval color="#0077b6" height={80} width={80} />
                </div>
            )}

            {weather.error && (
                <div className="error-message">
                    <FontAwesomeIcon icon={faFrown} />
                    <span>Ville introuvable. Veuillez réessayer.</span>
                </div>
            )}

            {weather.data && (
                <div className="weather-info">
                    <h2>
                        {weather.data.name}, {weather.data.sys.country}
                    </h2>
                    <span>{toDateFunction()}</span>
                    <img
                        src={`https://openweathermap.org/img/wn/${weather.data.weather[0].icon}@2x.png`}
                        alt={weather.data.weather[0].description}
                    />
                    <p>{Math.round(weather.data.main.temp)}°C</p>
                    <p>Vitesse du vent : {weather.data.wind.speed} m/s</p>
                </div>
            )}
        </div>
    );
}

export default Grp204WeatherApp;
