import React, { useState } from 'react';
import axios from 'axios';
import { Oval } from 'react-loader-spinner';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFrown } from '@fortawesome/free-solid-svg-icons';
import './App.css';

function Grp204WeatherApp() {
    const [input, setInput] = useState('');
    const [weather, setWeather] = useState({
        loading: false,
        data: null,
        forecast: null,
        error: false,
    });

    const apiKey = 'f00c38e0279b7bc85480c3fe775d518c';

    const toDateFunction = () => {
        const months = [
            'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin',
            'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre',
        ];
        const weekdays = [
            'Dimanche', 'Lundi', 'Mardi', 'Mercredi', 'Jeudi', 'Vendredi', 'Samedi',
        ];
        const currentDate = new Date();
        const date = `${weekdays[currentDate.getDay()]} ${currentDate.getDate()} ${months[currentDate.getMonth()]}`;
        return date;
    };

    const search = async (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            if (!input.trim()) {
                setWeather({ ...weather, error: true });
                return;
            }
            setWeather({ loading: true, data: null, forecast: null, error: false });

            const weatherUrl = 'https://api.openweathermap.org/data/2.5/weather';

            try {
                // Récupération des données météo actuelles
                const currentWeather = await axios.get(weatherUrl, {
                    params: {
                        q: input,
                        units: 'metric',
                        appid: apiKey,
                    },
                });

                const { coord } = currentWeather.data;

                //  Feature 1 : Prévisions météo sur plusieurs jours : Récupération des prévisions à 7 jours

                const forecastUrl = 'https://api.openweathermap.org/data/2.5/onecall';
                const forecastWeather = await axios.get(forecastUrl, {
                    params: {
                        lat: coord.lat,
                        lon: coord.lon,
                        exclude: 'current,minutely,hourly',
                        units: 'metric',
                        appid: apiKey,
                    },
                });

                setWeather({
                    loading: false,
                    data: currentWeather.data,
                    forecast: forecastWeather.data.daily,
                    error: false,
                });

                setInput(''); // Réinitialiser le champ de saisie
            } catch (err) {
                setWeather({ loading: false, data: null, forecast: null, error: true });
            }
        }
    };

    return (
        <div className="App">
            <h1 className="app-name">Application Météo GRP204</h1>
            <div className="search-bar">
                <input
                    type="text"
                    className="city-search"
                    placeholder="Entrez le nom de la ville..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={search}
                />
            </div>

            {weather.loading && (
                <div className="loader">
                    <Oval color="#0077b6" height={80} width={80} />
                </div>
            )}

            {weather.error && (
                <div className="error-message">
                    <FontAwesomeIcon icon={faFrown} />
                    <span>Ville introuvable. Veuillez réessayer.</span>
                </div>
            )}

            {weather.data && (
                <div className="weather-info">
                    <h2>
                        {weather.data.name}, {weather.data.sys.country}
                    </h2>
                    <span>{toDateFunction()}</span>
                    <img
                        src={`https://openweathermap.org/img/wn/${weather.data.weather[0].icon}@2x.png`}
                        alt={weather.data.weather[0].description}
                    />
                    <p>{Math.round(weather.data.main.temp)}°C</p>
                    <p>Vitesse du vent : {weather.data.wind.speed} m/s</p>
                </div>
            )}

            {weather.forecast && (
                <div className="forecast-container">
                    <h2>Prévisions sur 7 jours</h2>
                    <div className="forecast-grid">
                        {weather.forecast.map((day, index) => (
                            <div className="forecast-item" key={index}>
                                <p>{new Date(day.dt * 1000).toLocaleDateString('fr-FR', { weekday: 'long' })}</p>
                                <img
                                    src={`https://openweathermap.org/img/wn/${day.weather[0].icon}@2x.png`}
                                    alt={day.weather[0].description}
                                />
                                <p>{Math.round(day.temp.day)}°C</p>
                                <p>{day.weather[0].description}</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default Grp204WeatherApp;
